import React, { useState } from 'react' 
import { useNavigate } from 'react-router-dom';
import axios from 'axios'
function Facultylogin({handleShow}) {
  const navigate=useNavigate();
  const [ id, setId] = useState('')
  const [password, setPassword] = useState('')
  const validate =()=>{
    if(id !== '' && password!==''){
      handleLogin();
    }
    else{
      alert("Enter valid credentials")
    }
  }
  const handleLogin = async() => {
    const response = await axios.post("http://172.17.15.186:3006/login", {id: id , password : password}) 
    if(response.status===200){
      handleShow();
      // navigate("/facultydashboard")
     // navigate("/layout")
    }
    else{
      alert("Credentials are incorrect. Enter valid cre")
    }
  } 
  const gettingname =(e)=>{
    setId(e.target.value)
  } 
  const gettingpassword =(e)=>{
    setPassword(e.target.value)
  }
  return (
    <div className='flex flex-row justify-center mt-40 '>
      <div className="basis-2/5 ">
    <img src="https://www.vidyalayaschoolsoftware.com/webassets/images/call_action.svg"/>
</div>
<div  className="basis -3/5 ml-20 mt-20 flex flex-col justify-center">
      <label className="text-m">Faculty Id</label>
        <br/>
            <input type="text" placeholder="Enter your name" 
            onChange={gettingname}
            className="border border-red-400 rounded"/> 
            <br/>

            <label className="text-m">Password</label>
            <br/>

            <input type="password" placeholder="Enter your password"   onChange={gettingpassword}
            className="border border-red-400 rounded"/>
            <br/>
            <br/><br/>
            <button 
            onClick = {validate}
            type="submit"
             className="bg-red-400 text-white rounded px-2 mt-4 "
             >Login
             </button>
    </div>
    </div>
  )
}

export default Facultylogin
